<script>
  import { onMount } from 'svelte';

  onMount(() => {
    animateStars();
  });

  const animateStars = () => {
    let stars = Array.from(document.querySelectorAll('.star'));
		if (stars) {
			shuffle(stars);
			for (let i = 0; i < stars.length; i++) {
				setTimeout(() => {
					stars[i].classList.add('star-animated');
				}, 2000 * i + Math.random() * 1000);
			}
		}
  }
  
  const shuffle =(array) => {
		array.sort(() => .5 - Math.random());
	};
</script>

